import React from "react";

const conteudos = [
  {
    conteudo: 'High Order Functions',
    bloco: 8,
    status: 'Aprendido'
  },
  {
    conteudo: 'Composicao de Componentes',
    bloco: 11,
    status: 'Aprendendo',
  },
  {
    conteudo: 'Composicao de Estados',
    bloco: 12,
    status: 'Aprenderei'
  },
  {
    conteudo: 'Redux',
    bloco: 16,
    status: 'Aprenderei'
  },
];

class Content extends React.Component {
  render() {
    <div>
      <p>O conteúdo é: {conteudos.map((e) => e.conteudo)}</p>
      <p>Status: {conteudos.map((e) => e.status)}</p>
      <p>Bloco: {conteudos.map((e) => e.bloco)}</p>
    </div>
  }
} 

export default Content;
